constexpr int mod = 1e9 + 7;

// mÇñ@Ç∆Ç∑ÇÈaÇÃãtå≥
// O(log a)
inline LL invMod(LL a, int m = mod) {
	LL b = m, u = 1, v = 0;
	while (b) {
		LL t = a / b;
		swap(a -= t * b, b);
		swap(u -= t * v, v);
	}
	return (u % m + m) % m;
}


constexpr int SZ = 1123456;
LL fact[SZ];
LL rfact[SZ];
struct fact_ {
	fact_() {
		fact[0] = 1;
		for (int i = 1; i<SZ; i++)
			fact[i] = fact[i - 1] * i%mod;
		rfact[SZ - 1] = invMod(fact[SZ - 1]);
		for (int i = SZ - 1; i>0; i--) {
			rfact[i - 1] = rfact[i] * i%mod;
		}
	}
}fact_init;


// ëgÇ›çáÇÌÇπnCk (mod mod)
// O(1)
LL Comb(LL n, LL k) {
	LL u = fact[n];
	LL d = (rfact[k] * rfact[n - k]) % mod;
	return u * d%mod;
}


class ChangingChange {
public:
	vector <int> countWays(vector <int>, vector <int>, vector <int>);
};

LL w[2123];
int D;
LL solve(int a, int n) {
	vector<int> pos;
	for (int d = D - 1; d >= 0; d-=a) {
		pos.push_back(d);
	}
	LL ret = 0;
	for (int i : range(pos.size())) {
		if (i%2==0) {
			ret += Comb(i+n-1, n-1)*w[pos[i]] % mod;
		}
		else {
			ret += mod - Comb(i+n-1, n-1)*w[pos[i]] % mod;
		}
	}
	return ret % mod;
}
